mapper.update:
UPDATE 
	t_user_info
SET
	password=#{password}
WHERE 
	id = #{id}

mapper.select:
SELECT t1.*,t2.id AS role_id,t2.name AS role_name FROM t_user_info t1,t_role_info t2
WHERE 1=1
	AND t1.role_id = t2.id
	
		AND t1.username LIKE CONCAT('%',#{username},'%') 
	
	
		AND t1.role_id = #{role_id}
	
	
		AND t1.id = #{user_id}
	
	
		AND t1.isLock = #{isLock}
	
	
		AND t1.name LIKE CONCAT('%', #{name}, '%')
	
ORDER BY t1.id DESC

SELECT * FROM t_rss_info WHERE 1=1
and title like concat('%', #{title}, '%')
and url like concat('%', #{url}, '%')
and id = #{id}
and user_id = #{user_id}

SELECT t1.*,t2.id AS role_id,t2.name AS role_name FROM t_user_info t1,t_role_info t2
WHERE 1=1
	AND t1.role_id = t2.id
	
		AND t1.username LIKE CONCAT('%',#{username},'%') 
	
	
		AND t1.role_id = #{role_id}
	
	
		AND t1.id = #{user_id}
	
	
		AND t1.isLock = #{isLock}
	
	
		AND t1.name LIKE CONCAT('%', #{name}, '%')
	
ORDER BY t1.id DESC

SELECT COUNT(1) FROM t_user_info t1,t_role_info t2
WHERE 1=1
	AND t1.role_id = t2.id
	
		AND t1.username LIKE CONCAT('%',#{username},'%') 
	
	
		AND t1.role_id = #{role_id}
	
	
		AND t1.id = #{user_id}
	
	
		AND t1.isLock = #{isLock}
	
	
		AND t1.name LIKE CONCAT('%', #{name}, '%')

SELECT * FROM t_role_info t1 WHERE 1=1
AND t1.id = #{id}
ORDER BY t1.id DESC

mapper.insert:

	SELECT LAST_INSERT_ID() AS newId

INSERT INTO t_role_info 
(
  name
) 
VALUES
(
  #{name}
)

mapper.update:
UPDATE 
	t_role_info
SET 
  name = #{name}
WHERE id = #{id}
